class MyApp extends StatelessWidget {
  const MyApp({super.key});
  // This widget is the root of your application.
  @override
  Widget build(BuildContext context) {
    getMaterialAppCalled = true;
    // Start MaterialApp
    return GetBuilder<LocalizationController>(
      builder: (LocalizationController localizationController) {
        return GetMaterialApp(
          initialRoute: '/',
          locale: localizationController.locale,
          translations:
              Messages(languages: localizationController.translations),
          title: 'skillbuddy',
          defaultTransition: Transition.rightToLeft,
          getPages: <GetPage<void>>[
            GetPage<void>(
              name: '/',
              page: () => const SplashPage(),
            ),
          ],
          color: const Color.fromRGBO(0, 78, 235, 1),
          theme: Get.put<ThemeService>(ThemeService()).themeData,
        );
        // End MaterialApp
      },
    );
  }
}
